import { getItems, getIntent } from "../apis/ncr";
import { MessageContext } from "../Context";
import React from "react";

const categories = ["Milk Tea", "Chocolate", "Oolong"];
const getCategory = (intent) => {
  for (let category of categories) {
    if (category.toLowerCase() === intent) {
      return category;
    }
  }
  return null;
};

export const useVoiceCommands = ({
  transcript,
  resetTranscript,
  resetVoice,
}) => {
  const { selectItems, addItemOrder } = React.useContext(MessageContext);
  React.useEffect(() => {
    if (transcript === "") return;
    getIntent(transcript)
      .then((res) => res.json())
      .then((res) => {
        const { intent } = res;
        console.log(intent);
        setTimeout(() => {
          if (intent === "menu") {
            getItems()
              .then((res) => res.json())
              .then((res) => selectItems(res.Result));
            resetVoice("Here is our Menu");
            resetTranscript();
          } else if (getCategory(intent)) {
            const category = getCategory(intent);
            getItems()
              .then((res) => res.json())
              .then((res) => {
                const result = res.Result.filter((r) =>
                  r.Name.includes(category)
                );
                selectItems(result);
              });
            resetVoice(`Here is our ${category}`);

            resetTranscript();
          } else if (intent === "order") {
            const name = transcript.substr("I want to order".length);
            getItems()
              .then((res) => res.json())
              .then((res) => {
                const result = res.Result.filter((r) =>
                  r.Name.toLowerCase().includes(name)
                );
                addItemOrder()
                console.log(result[0]);
              });
            resetVoice("I am listening");
          }
        }, 2000);
      });
  }, [transcript, resetVoice, resetTranscript, selectItems]);
};
