import { getItems } from "../apis/ncr";
import { MessageContext } from "../Context";
import React from "react";

export const useVoiceCommands = ({
  transcript,
  resetTranscript,
  resetVoice,
}) => {
  const { selectItems } = React.useContext(MessageContext);
  React.useEffect(() => {
    setTimeout(() => {
      if (transcript === "show me the menu") {
        getItems()
          .then((res) => res.json())
          .then((res) => selectItems(res.Result));
        resetVoice("Here is our Menu");
        resetTranscript();
      } else if (
        transcript.includes("milk tea") ||
        transcript.includes("oolong")
      ) {
        const category = transcript.includes("milk tea") ? "Milk Tea" : "";
        getItems()
          .then((res) => res.json())
          .then((res) => {
            console.log(res);
            const result = res.Result.filter((r) =>
              r.Name.includes(category)
            );
            selectItems(result);
          });
        resetVoice(`Here is our Milk Tea`);

        resetTranscript();
      } else if (transcript.startsWith("I want to order")) {
        const name = transcript.substr("I want to order".length);
        console.log(name);
        resetVoice("I am listening");
      }
    }, 2000);
  }, [transcript, resetVoice, resetTranscript, selectItems]);
};
