import React from "react";
import { Dictaphone, ShowItems } from "./components";
import { MessageProvider } from "./Context";
import Badge from "@material-ui/core/Badge";
import MailIcon from "@material-ui/icons/Mail";

function App() {
  const [items, setItems] = React.useState([]);
  const [orders, addOrders] = React.useState([]);

  const selectItems = (i) => {
    setItems(i);
  };

  const addItemOrder = (order) => {
    console.log(order.Name);
    console.log(orders);
    // if (orders.length > 0) {
    //   console.log(order.Name);
    //   console.log(orders[orders.length - 1].Name);
    //   if (order.Name === orders[orders.length - 1].Name) return;
    // }
    addOrders((prev) => {
      if (prev[prev.length - 1].Name === order.Name) {
        return [prev];
      }
      return [...prev, order];
    });
  };

  return (
    <MessageProvider value={{ items, selectItems, addItemOrder }}>
      <Badge
        badgeContent={orders.length}
        color="primary"
        style={{ marginTop: 20 }}
      >
        <MailIcon />
      </Badge>
      <Dictaphone />
      <ShowItems />
    </MessageProvider>
  );
}

export default App;
