import { getItems, getIntent } from "../apis/ncr";
import { MessageContext } from "../Context";
import React from "react";

const categories = ["Milk Tea", "Chocolate", "Oolong"];
const getCategory = (intent) => {
  for (let category of categories) {
    if (category.toLowerCase() === intent) {
      return category;
    }
  }
  return null;
};

export const useVoiceCommands = ({
  transcript,
  resetTranscript,
  resetVoice,
}) => {
  const { selectItems, addItemOrder, orders, setTotalPrice } = React.useContext(
    MessageContext
  );
  React.useEffect(() => {
    if (transcript === "") return;
    setTimeout(() => {
      getIntent(transcript)
        .then((res) => res.json())
        .then((res) => {
          const { intent } = res;
          if (intent === "menu") {
            getItems()
              .then((res) => res.json())
              .then((res) => {
                selectItems(res.Result);
                resetVoice("Here is our Menu");
                resetTranscript();
                setTotalPrice(false);
              });
          } else if (getCategory(intent)) {
            const category = getCategory(intent);
            getItems()
              .then((res) => res.json())
              .then((res) => {
                const result = res.Result.filter((r) =>
                  r.Name.includes(category)
                );
                selectItems(result);
                resetVoice(`Here is our ${category}`);
                resetTranscript();
                setTotalPrice(false);
              });
          } else if (intent.includes("order_")) {
            const name = intent.substr("order_".length).replace(/#|_/g, " ");
            getItems()
              .then((res) => res.json())
              .then((res) => {
                for (let r of res.Result) {
                  if (r.Name.toLowerCase().trim() === name.trim()) {
                    addItemOrder(r);
                    break;
                  }
                }
                selectItems([]);
                resetVoice("Ok, I added your order.");
                resetTranscript();
              });
          } else if (intent.includes("show order")) {
            console.log("OK bish");
            console.log(orders);
            selectItems(orders);
            setTotalPrice(true);
          }
        });
    }, 3000);
  }, [
    addItemOrder,
    orders,
    resetTranscript,
    resetVoice,
    selectItems,
    setTotalPrice,
    transcript,
  ]);
};
