import React from "react";
import { Dictaphone, ShowItems } from "./components";
import { MessageProvider } from "./Context";
import Badge from "@material-ui/core/Badge";
import MailIcon from "@material-ui/icons/Mail";

function App() {
  const [items, setItems] = React.useState([]);
  const [orders, addOrders] = React.useState([]);
  const [init, setI] = React.useState(false);

  const selectItems = (i) => {
    setItems(i);
  };
  const setInit = (init) => {
    setI(init);
  };
  const addItemOrder = (order) => {
    if (orders.length !== 0) {
      if (order.Name === orders[orders.length - 1].Name) return;
    }
    addOrders((prev) => [...prev, order]);
  };

  return (
    <MessageProvider
      value={{ items, selectItems, addItemOrder, init, setInit }}
    >
      <Badge
        badgeContent={orders.length}
        color="primary"
        style={{ marginTop: 20 }}
      >
        <MailIcon />
      </Badge>
      <Dictaphone />
      <ShowItems />
    </MessageProvider>
  );
}

export default App;
