import React from "react";
import { Dictaphone, ShowItems } from "./components";
import { MessageProvider } from "./Context";
import Badge from "@material-ui/core/Badge";
import MailIcon from "@material-ui/icons/Mail";
import { getItems } from "./apis";

function App() {
  const [items, setItems] = React.useState([]);
  const [orders, addOrders] = React.useState([]);
  const selectItems = (i) => {
    setItems(i);
  };
  const addItemOrder = (order) => {
    addOrders((prev) => [...prev, order]);
  };

  React.useEffect(() => {
    getItems()
      .then((res) => res.json())
      .then((res) => {
        selectItems(res.Result);
        resetVoice("Here is our Menu");
        resetTranscript();
      });
  }, []);
  return (
    <MessageProvider value={{ items, selectItems, addItemOrder }}>
      <Badge
        badgeContent={orders.length}
        color="primary"
        style={{ marginTop: 20 }}
      >
        <MailIcon />
      </Badge>
      <Dictaphone />
      <ShowItems />
    </MessageProvider>
  );
}

export default App;
