import { getItems } from "../apis/ncr";
import { MessageContext } from "../Context";
import React from "react";

export const useVoiceCommands = ({
  transcript,
  resetTranscript,
  resetVoice,
}) => {
  const { selectItems } = React.useContext(MessageContext);
  React.useEffect(() => {
    if (transcript === "show me the menu") {
      getItems()
        .then((res) => res.json())
        .then((res) => selectItems(res.Result));
      resetTranscript();
      resetVoice("Here is our Menu");
    } else if (transcript.includes("milk tea")) {
      getItems()
        .then((res) => res.json())
        .then((res) => {
          console.log(res);
          const result = res.Result.filter((r) => r.Name.includes("Milk Tea"));
          selectItems(result);
        });
      resetTranscript();
      resetVoice("Here is our Milk Tea");
