import React, { useEffect } from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import { useVoiceCommands } from "../data/useVoiceCommands";
import MicIcon from "@material-ui/icons/Mic";
import Speech from "react-speech";

export const Dictaphone = () => {
  const { transcript, resetTranscript } = useSpeechRecognition();
  const [voice, setVoice] = React.useState("I am listening...");

  const resetVoice = (str) => {
    setVoice(str);
  };

  useVoiceCommands({ transcript, resetTranscript, resetVoice });

  useEffect(() => {
    SpeechRecognition.startListening({ continuous: true });
  }, []);

  if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
    return null;
  }

  return (
    <>
      <Speech
        text="Welcome to react speech"
        pitch="1"
        rate="1"
        volume="1"
        lang="en-GB"
        voice="Google UK English Male"

      />
      <div style={{ display: "flex", flexDirection: "row" }}>
        <MicIcon />
        <span>{voice}</span>
      </div>
      <p>{transcript}</p>
    </>
  );
};
