const fetch = require("node-fetch");

export const getItems = () => {
  let fetch = require("node-fetch");

  let res = fetch(
    "https://api-reg-apigee.ncrsilverlab.com/v2/inventory/items",
    {
      headers: {
        Accept: "application/json",
        Authorization: "Bearer " + getToken(),
      },
    }
  );
  return res;
};

export const getToken = () => {
  fetch("https://api-reg-apigee.ncrsilverlab.com/v2/oauth2/token", {
    headers: {
      Accept: "application/json",
      client_id: "AST00004500",
      client_secret:
        "y6vjOQ3G5tVSAmbStlhhWkgM56gi7oSm3EtCi214L4Xht9TtPoDQ3r6Hvrr10n1terphYV6VHnXVMVwRCNcc7Att",
    },
  });
};
