import React from "react";
import { MessageContext } from "../Context";

export const ShowItems = () => {
  const { items } = React.useContext(MessageContext);
  if (items.length === 0) return null;
  return (
    <>
      {items.map((item, idx) => {
        return <div key={idx}>{item}</div>;
      })}
    </>
  );
};
